import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Pav Bhaji",
    description: "Spicy flavoured vegetable curry served with Indian bread",
    price: 150,
  },
  {
    id: "m2",
    name: "Italian Pizza",
    description: "A world famous Italian delicacy!",
    price: 340,
  },
  {
    id: "m3",
    name: "Veg Burger",
    description: "Fresh veggies with potato patty in bun bread",
    price: 230,
  },
  {
    id: "m4",
    name: "Paneer Biryani",
    description: "Indian styled rice with spices and paneer",
    price: 350,
  },
];

const AvailableMeals = () => {
  const mealsList = DUMMY_MEALS.map((meal) => <li>{meal.name}</li>);
  return (
    <section className={classes.meals}>
      <ul>
        <Card>{mealsList}</Card>
      </ul>
    </section>
  );
};

export default AvailableMeals;
